import { AppBlock, events } from "@slflows/sdk/v1";
import { KMSClient, VerifyCommand } from "@aws-sdk/client-kms";

const verify: AppBlock = {
  name: "Verify",
  description:
    "Verifies a digital signature that was generated by the Sign operation.",
  inputs: {
    default: {
      config: {
        region: {
          name: "Region",
          description: "AWS region for this operation",
          type: "string",
          required: true,
        },
        KeyId: {
          name: "Key Id",
          description:
            "Identifies the asymmetric KMS key that will be used to verify the signature.",
          type: "string",
          required: true,
        },
        Message: {
          name: "Message",
          description: "Specifies the message that was signed.",
          type: "string",
          required: true,
        },
        MessageType: {
          name: "Message Type",
          description:
            "Tells KMS whether the value of the Message parameter should be hashed as part of the signing algorithm.",
          type: "string",
          required: false,
        },
        Signature: {
          name: "Signature",
          description: "The signature that the Sign operation generated.",
          type: "string",
          required: true,
        },
        SigningAlgorithm: {
          name: "Signing Algorithm",
          description:
            "The signing algorithm that was used to sign the message.",
          type: "string",
          required: true,
        },
        GrantTokens: {
          name: "Grant Tokens",
          description: "A list of grant tokens.",
          type: {
            type: "array",
            items: {
              type: "string",
            },
          },
          required: false,
        },
        DryRun: {
          name: "Dry Run",
          description: "Checks if your request will succeed.",
          type: "boolean",
          required: false,
        },
      },
      onEvent: async (input) => {
        const { region, ...commandInput } = input.event.inputConfig;

        const client = new KMSClient({
          region: region,
          credentials: {
            accessKeyId: input.app.config.accessKeyId,
            secretAccessKey: input.app.config.secretAccessKey,
            sessionToken: input.app.config.sessionToken,
          },
        });

        const command = new VerifyCommand(commandInput as any);
        const response = await client.send(command);

        await events.emit(response || {});
      },
    },
  },
  outputs: {
    default: {
      name: "Verify Result",
      description: "Result from Verify operation",
      possiblePrimaryParents: ["default"],
      type: {
        type: "object",
        properties: {
          KeyId: {
            type: "string",
            description:
              "The Amazon Resource Name (key ARN) of the asymmetric KMS key that was used to verify the signature.",
          },
          SignatureValid: {
            type: "boolean",
            description:
              "A Boolean value that indicates whether the signature was verified.",
          },
          SigningAlgorithm: {
            type: "string",
            description:
              "The signing algorithm that was used to verify the signature.",
          },
        },
        additionalProperties: true,
      },
    },
  },
};

export default verify;
